import React, { useState, useEffect } from 'react';
import './styles.css';


function TeamListing(props) {

    const [team, setTeam] = useState(false)

    useEffect(() => {
        console.log(props.team.team)
        console.log(props.team.team.length)
        for(let i = 0; i < props.team.team.length; i++) {
            fetch("/api" + props.team.team[i])
            .then((res) => res.json())
            .then((data) => {
                if(team == false) {
                    setTeam([data.sprites.front_default])
                    console.log('THIS SHOWS ONCE')
                } else {
                    setTeam(...team, data.sprites.front_default)
                    console.log('THIS SHOWS FIVE TIMES')
                }
            })
        }
        console.log(team)
      }, []);

  return (
    <div className="select-wrapper">
        <h3>{props.name}</h3>
        <div className="team-container">
        {team.length > 5 ? 
            team.map((pokemon, i) => {
                return (
                    <img src={pokemon} key={i} />
                )
            })
        : null}
        </div>
    </div>
  );
}

export default TeamListing;
