import React, { useState, useEffect } from 'react';
import './styles.css';
import PokemonSelect from '../PokemonSelect/index'


function PokemonWrap() {
    let newTeamArray = ['Pokéawesome', 'PETA\'s Nightmare', 'Frightning Fellows', 'Green Team', 'Deadly Alliance', 'Attack Squadron', 'Violent Bunch', 'Friendly Foes']

    const [name, setName] = useState(newTeamArray[Math.floor(Math.random() * 8) + 1])

    const rng = () => Math.floor(Math.random() * 151) + 1


  return (
    <div className="select-wrapper">
    <h2>{name.length < 1 ? '(unnamed)' : name}</h2>
        <div className="select-container">
            <PokemonSelect index={rng}/>
            <PokemonSelect index={rng}/>
            <PokemonSelect index={rng}/>
            <PokemonSelect index={rng}/>
            <PokemonSelect index={rng}/>
            <PokemonSelect index={rng}/>
        </div>
        <form className="team-form" action="/new-team" method="POST">
            <input type="text" name="title" value={name} onChange={(e) => {setName(e.target.value)}}/>
            <button type="submit">Add Team</button>
        </form>
    </div>
  );
}

export default PokemonWrap;
