import React, { useState, useEffect } from 'react';
import './styles.css';


function PokemonSelect() {
  const capitalize = (x) => {
    x[0] = x[0].toUpperCase()
  }

  const [data, setData] = useState(null)
  const [index, setIndex] = useState(1)

  useEffect(() => {
    fetch("/api" + index.toString())
      .then((res) => res.json())
      .then((data) => setData(data))
  }, [index]);

  return (
    <div className="selector">
        {data ? <img src={data.sprites.front_default} /> : null}
        <p>{data ? capitalize(data.name) : 'loading...'}</p>
        <button onClick={() => {
          if(index > 1) {
            console.log(index)
            setIndex(index - 1)
          }
        }} >Down One</button>
        <button onClick={() => {
          if(index < 151) {
            console.log(index)
            setIndex(index + 1)
          }
        }} >Up One</button>
    </div>
  );
}

export default PokemonSelect;
