import './styles.css';


function TeamListing(props) {

    const [team, setTeam] = useState(null)

    useEffect(() => {
        let teamArray = []
        for(let i = 0; i < props.team.length; i++) {
            fetch("/api" + props.team[i])
            .then((res) => res.json())
            .then((data) => teamArray.push(data.sprites.front_default))
            setTeam(teamArray)
        }
      }, []);

