import React, { useState, useEffect } from 'react';
import './styles.css';


function TeamListing(props) {

    const [team, setTeam] = useState(false)

    useEffect(() => {
        console.log('Hello')
        let teamArray = []
        for(let i = 0; i < props.team.length; i++) {
            fetch("/api" + props.team[i])
            .then((res) => res.json())
            .then((data) => teamArray.push(data.sprites.front_default))
            setTeam(teamArray)
            console.log(teamArray)
        }
      }, []);

  return (
    <div className="select-wrapper">
        <h3>{props.name}</h3>
        <div className="team-container">
        {team.length > 0 ? 
            team.map((pokemon, i) => {
                return (
                    <img src={pokemon} />
                )
            })
        : null}
        </div>
    </div>
  );
}

export default TeamListing;
