import React, { useState, useEffect } from 'react';
import './styles.css';


function PokemonSelect() {
  const capitalize = (string) => {
    return string.charAt(0).toUpperCase() + string.slice(1);
  }

  const [data, setData] = useState(null)
  const [index, setIndex] = useState(Math.floor(Math.random() * 151) + 1  )

  useEffect(() => {
    fetch("/api" + index.toString())
      .then((res) => res.json())
      .then((data) => setData(data))
  }, [index]);

  return (
    <div className="selector">
    <div 
      className="arrow"
      style={{borderTopLeftRadius: 10, borderBottomLeftRadius: 10,}}
      onClick={() => {
        if(index > 1) {
          console.log(index)
          setIndex(index - 1)
        }
      }}
    ><p>&#8249;</p></div>
    <div>
        {data ? <img src={data.sprites.front_default} style={{marginTop: 400}}/> : null}
        <p>{data ? capitalize(data.name) : 'loading...'}</p>
    </div>
    <div 
      className="arrow"
      style={{borderTopRightRadius: 10, borderBottomRightRadius: 10,}}
      onClick={() => {
        if(index < 151) {
          console.log(index)
          setIndex(index + 1)
        }
      }}
    ><p>&#8250;</p></div>
    </div>
  );
}

export default PokemonSelect;
