import React, { useState, useEffect } from 'react';
import './styles.css';
import TeamSprite from './TeamSprite/index'


function TeamListing(props) {

    const [edit, setEdit] = useState(false)

    const [index, setIndex] = useState([1, 2, 3, 4, 5, 6])

  return (
    <div className="select-wrapper">
        <h3>{props.name}</h3>
        <div className="team-container">
            <TeamSprite index={props.team[0]} />
            <TeamSprite index={props.team[1]} />
            <TeamSprite index={props.team[2]} />
            <TeamSprite index={props.team[3]} />
            <TeamSprite index={props.team[4]} />
            <TeamSprite index={props.team[5]} />
        </div>
        <div className="control-panel">
            <button className="team-button control-button" onClick={() => setEdit(!edit)}>Edit</button>
            <button className="team-button control-button" onClick={() => {
                fetch('/delete-team', {
                    method: 'delete',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({
                        id: props.id
                    })
                })
                window.location.reload(false)
            }}>
            Delete
            </button>
        </div>
        {edit ? <div className="editPanel">
            <div>
                <p>Pok√©mon Indexes: </p>
                <input type="number" id="quantity" name="quantity" min="1" max="5" />
            </div>
            <div><p>Team Name: </p></div>
        </div> : null}
    </div>
  );
}

export default TeamListing;
